mapper.select:
select * from tb_menu
where 1=1

    and deleted = #{deleted}


    and biz_id = #{bizId}


    and parent_id = #{parentId}


    and level = #{level}

mapper.insert:
insert into tb_account_menu(
biz_id,
account_biz_id

    ,checked_key


    ,half_checked_key


    ,is_new

) values(
#{bizId},
#{accountBizId}

    ,#{checkedKey}


    ,#{halfCheckedKey}


    ,#{isNew}

)
